import React from 'react';
import Button from '../Button/Button';
import './FirmFactCard.scss';

interface FirmFactCardProps {
  variant: 'default' | 'border'; // Type for the variant prop
  buttonText: string; // Button text passed as prop
  icon: boolean; // Pass icon visibility from FirmFacts
}

const FirmFactCard: React.FC<FirmFactCardProps> = ({ variant, buttonText, icon }) => {
  return (
    <div className={`firmFactCard ${variant}`}>
      <div className="card-content">
        <Button variant="default" text={buttonText} icon={icon} /> {/* Pass icon prop */}
      </div>
    </div>
  );
};

export default FirmFactCard;
