import React from 'react';
import FirmFactCard from '../FirmFactCard/FirmFactCard';
import './FirmFacts.scss';

interface Card {
  variant: 'default' | 'border'; // Explicitly define the type of variant
  text: string; // Button text
  icon: boolean; // Pass whether the button should have a search icon
}

const FirmFacts: React.FC = () => {
  // Data for the cards with variant, text, and icon visibility
  const cards: Card[] = [
    { variant: 'default', text: 'This is a one line button', icon: false }, // No icon
    { variant: 'border', text: 'This is a two line button that terminates with ...', icon: true }, // With icon
    { variant: 'default', text: 'This is a one line button', icon: false }, // No icon
    { variant: 'border', text: 'This is a two line button that terminates with ...', icon: true }, // With icon
    { variant: 'default', text: 'This is a one line button', icon: false }, // No icon
    { variant: 'border', text: 'This is a two line button that terminates with ...', icon: true }, // With icon
    { variant: 'border', text: 'This is a two line button that terminates with ...', icon: false } // No icon
  ];

  return (
    <div className="firmFacts-wrapper">
      <div className="firmFacts-title">
        <h2>Firm Facts</h2>
        <button className="close-button">X</button>
      </div>
      <div className="firmFacts">
        {cards.map((card, index) => (
          <FirmFactCard
            key={index}
            variant={card.variant}
            buttonText={card.text}
            icon={card.icon} // Pass icon visibility to FirmFactCard
          />
        ))}
      </div>
    </div>
  );
};

export default FirmFacts;
