// src/components/Button/Button.tsx
import React from 'react';
import './Button.scss';

type ButtonProps = {
  text: string;
  icon?: string;  // Optionally pass an icon
  disabled?: boolean;
  isTwoLine?: boolean; // New prop to check if it's a two-line button
};

const Button: React.FC<ButtonProps> = ({ text, icon, disabled, isTwoLine }) => {
  return (
    <button
      className={`button ${isTwoLine ? 'two-lines' : ''} ${icon ? 'icon' : ''}`}
      disabled={disabled}
    >
      {icon && <span className={`icon ${icon}`}></span>}
      <span className="button-text">{text}</span>
    </button>
  );
};

export default Button;
