import React from 'react';
import './Button.scss';

interface ButtonProps {
  variant: 'default' | 'icon'; // Either default or icon variant
  text: string;
  disabled?: boolean;
  icon?: boolean; // Optional icon prop
}

const Button: React.FC<ButtonProps> = ({ variant, text, disabled, icon }) => {
  return (
    <button
      className={`button ${variant} ${icon ? 'with-icon' : ''}`}
      disabled={disabled}
    >
      {icon && <span className="icon">üîç</span>} {/* Search icon */}
      <span className="button-text">{text}</span>
    </button>
  );
};

export default Button;
