// src/components/FirmFacts/FirmFacts.tsx
import React from 'react';
import FirmFactCard from '../FirmFactCard/FirmFactCard';
import './FirmFacts.scss';

const FirmFacts: React.FC = () => {
  return (
    <div className="firmFacts">
      <div className="firmFacts__grid">
        {/* Double size FirmFactCard in the first column */}
        <div className="firmFacts__item firmFacts__item--double">
          <FirmFactCard variant="default" />
        </div>
        {/* Small FirmFactCards */}
        {[...Array(6)].map((_, index) => (
          <div key={index} className="firmFacts__item">
            <FirmFactCard
              variant={index === 0 ? 'border' : 'default'} // Example: First card with border variant
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default FirmFacts;
